const config = require('./config/config');

import express, { Application } from 'express';
import cors from 'cors';
import helmet from 'helmet';
import cookieParser from 'cookie-parser';

import routes from './routes';
import ErrorHandler from './middlewares/error.middleware';
import Logger from './config/logger';

import morgan from 'morgan';

//swagger
const swaggerUi = require('swagger-ui-express');
const swaggerDocument = require('./doc/swagger-output.json');

class App {
  public app: Application;
  public host: string | number;
  public port: string | number;
  public api_version: string | number;
  public env: boolean;
  private logStream = Logger.logStream;
  private logger = Logger.logger;
  public errorHandler = new ErrorHandler();
  
  constructor() {
    this.app = express();
    this.host = config.development.host;
    this.port = config.development.app_port;
    this.api_version = config.development.version;

    this.initializeMiddleWares();
    this.initializeRoutes();
    this.initializeErrorHandlers();
    this.startApp();
  }

  public initializeMiddleWares(): void {
    this.app.use(cors());
    this.app.use(helmet());
    this.app.use(express.urlencoded({ extended: true }));
    this.app.use(express.json());
    this.app.use(morgan('combined', { stream: this.logStream }));
    this.app.use(cookieParser());
  }

  public initializeRoutes(): void {
    this.app.use(`/api/${this.api_version}`, routes());
    this.app.use('/api-docs', swaggerUi.serve, swaggerUi.setup(swaggerDocument));
    this.app.get('/test', (req, res) => {
      res.send('Test route is working');
    });
  }

  public initializeErrorHandlers(): void {
    this.app.use(this.errorHandler.appErrorHandler);
    this.app.use(this.errorHandler.genericErrorHandler);
    this.app.use(this.errorHandler.notFound);
  }

  public startApp(): void {
    this.app.listen(this.port, () => {
      this.logger.info(
        `Server started at ${this.host}:${this.port}/api/${this.api_version}/`
      );
    });
  }

  public getApp(): Application {
    return this.app;
  }
}

const app = new App();

export default app;

import express, { IRouter } from 'express';
const router = express.Router();

import userRoute from './user.route';
import noteRoute from './notes.routes';
/**
 * Function contains Application routes
 *
 * @returns router
 */
const routes = (): IRouter => {
  
  router.get('/', (req, res) => {
    res.json('Welcome');
  });
  
  router.use('/users', new userRoute().getRoutes());

  router.use('/notes', new noteRoute().getRoutes());

  return router;
};

export default routes;

import express, { IRouter } from 'express';
import userController from '../controllers/user.controller';
import userValidator from '../validators/user.validator';
import { userAuth, forgotUser } from '../middlewares/auth.middleware';

class UserRoutes {
  private UserController = new userController();
  private router = express.Router();
  private UserValidator = new userValidator();

  constructor() {
    this.routes();
  } 

  private routes = () => { 

    this.router.get('/', userAuth,  this.UserController.getUser);

    this.router.post('/', this.UserValidator.newUser, this.UserController.register);
    
    this.router.post('/login', this.UserValidator.login, this.UserController.login);

    this.router.put('/', userAuth, this.UserValidator.update, this.UserController.updateUser);

    this.router.put('/password', userAuth, this.UserController.updateUserPassword);

    this.router.delete('/', userAuth, this.UserController.deleteUser);

    /////////////////////////////////////////////////////////////

    this.router.post('/forget',  this.UserController.forgetUser);

    this.router.post('/forget/reset', forgotUser, this.UserController.reset);

    ////////////////////////////////////////////////////////////

  };

  public getRoutes = (): IRouter => {
    return this.router;
  };
}

export default UserRoutes;

