      }
    },
    {
      sequelize,
      modelName: 'user',
      schema: 'fundoonotes',
      timestamps: false,
      hooks: {
        beforeCreate: async (user: User, options: any) => {
          if (user.password) {
            user.password = await bcrypt.hash(user.password, 10);
          }
        },
        beforeUpdate: async (user: User, options: any) => {
          if (user.changed('password')) {
            user.password = await bcrypt.hash(user.password, 10);
          }
        },
      }
      
    }
  );
  
  return User;
};

