import HttpStatus from 'http-status-codes';
import Logger from '../config/logger';
import { Request, Response, NextFunction } from 'express';

class ErrorMiddleware {
  private logger;

  constructor() {
    this.logger = Logger.logger;
  }

  /**
   * Error response middleware for 404 not found.
   *
   * @param {Object} req
   * @param {Object} res
   */
  public notFound = (req: Request, res: Response): void => {
    res.status(HttpStatus.NOT_FOUND).json({
      code: HttpStatus.NOT_FOUND,
      message: 'Ooops, route not found'
    });
  };

  /**
   * Error response middleware for handling
   * all app errors except generic errors.
   *
   * @param  {Object}   err
   * @param  {Object}   req
   * @param  {Object}   res
   * @param  {Function} next
   */
  // eslint-disable-next-line no-unused-vars
  public appErrorHandler = (
    err: any,
    req: Request,
    res: Response,
    next: NextFunction
  ): void => {
    if (err.code && typeof err.code === 'number') {
      this.logger.error(`
      status - ${err.code}
      message - ${err.message} 
      url - ${req.originalUrl} 
      method - ${req.method} 
      IP - ${req.ip}
    `);
      res.status(err.code).json({
        code: err.code,
        message: err.message
      });
    } else {
      next(err);
    }
  };

  /**
   * Generic error response middleware for internal server errors.
   *
   * @param  {Object}   err
   * @param  {Object}   req
   * @param  {Object}   res
   * @param  {Function} next
   */
  // eslint-disable-next-line no-unused-vars
  public genericErrorHandler = (
    err: any,
    req: Request,
    res: Response,
    next: NextFunction
  ): void => {
    this.logger.error(`
    status - ${HttpStatus.INTERNAL_SERVER_ERROR} 
    message - ${err.stack} 
    url - ${req.originalUrl} 
    method - ${req.method} 
    IP - ${req.ip}
  `);

    res.status(HttpStatus.INTERNAL_SERVER_ERROR).json({
      code: HttpStatus.INTERNAL_SERVER_ERROR,
      data: '',
      message: err.message
    });
  };
}

export default ErrorMiddleware;

import winston, { format } from 'winston';
import 'winston-daily-rotate-file';

class Logger {
  /**
   * Logger handles all logs in the application
   */
  static logger = winston.createLogger({
    format: format.combine(format.timestamp(), format.simple()),
    transports: [
      new winston.transports.File({
        filename: 'logs/server/error.log',
        level: 'error',
        handleExceptions: true
      }),
      new winston.transports.File({
        filename: 'logs/server/all.log',
        level: 'info',
        handleExceptions: true
      }),
      new winston.transports.DailyRotateFile({
        maxFiles: '14d',
        level: 'info',
        dirname: 'logs/server/daily',
        datePattern: 'YYYY-MM-DD',
        filename: '%DATE%.log'
      }),
      new winston.transports.Console({
        level: 'debug',
        handleExceptions: true
      })
    ]
  });

  static logStream = {
    /**
     * A writable stream for winston logger.
     *
     * @param {any} message
     */
    write(message) {
      /**
       * morganLogger logs all http request in a dedicated file and on console
       */
      const morganLogger = winston.createLogger({
        format: format.combine(format.simple()),
        transports: [
          new winston.transports.File({
            filename: 'logs/requests/all.log',
            level: 'debug',
            handleExceptions: true
          }),
          new winston.transports.Console({
            level: 'debug',
            handleExceptions: true
          }),
          new winston.transports.DailyRotateFile({
            maxFiles: '14d',
            level: 'info',
            dirname: 'logs/requests/daily',
            datePattern: 'YYYY-MM-DD',
            filename: '%DATE%.log'
          })
        ]
      });
      morganLogger.info(message.toString());
    }
  };
}

export default Logger;
